package org.learn.leetcode.string;

import org.junit.Assert;
import org.junit.Test;

/**
 * https://leetcode.com/problems/is-subsequence/
 * Created by Administrator on 2016/9/9.
 */
public class IsSubsequence {
    public boolean isSubsequence(String s, String t) {
        int lastCharPosition = 0;
        int count = 0;
        for (int i = 0; i < s.length(); i++) {
            int index = t.indexOf(s.charAt(i), lastCharPosition);
            if (index < i) {
                return false;
            } else {
                lastCharPosition = index + 1;
                count++;
            }
        }
        return count == s.length();
    }

    @Test
    public void test() {
        Assert.assertEquals(false, isSubsequence("leeeeetcode", ""));
        Assert.assertEquals(true, isSubsequence("abc", "ahbgdc"));
    }
}
